mapper.update:
UPDATE CALENDAR
SET TITLE = #{title},
    START = #{start},
    END = #{end},
    START_TIME = #{startTime},
    END_TIME = #{endTime},
    CONTENTS = #{contents}
WHERE CALENDAR_SEQ = #{calendarSeq}
  AND USER_ID = #{userId}

mapper.delete:
DELETE FROM CALENDAR
WHERE CALENDAR_SEQ = #{calendarSeq}
  AND USER_ID = #{userId}

mapper.select:
SELECT START AS date FROM CALENDAR
WHERE START BETWEEN #{doMonth} AND #{nextMonth}
        AND USER_ID = #{userId}
